<?php include(__DIR__.'/config.php');if($_SERVER['REQUEST_METHOD']=='POST'){$id=$db->real_escape_string($_POST['id']);$nome=$db->real_escape_string($_POST['nome']);$endereco=$db->real_escape_string($_POST['endereco']);$bairro=$db->real_escape_string($_POST['bairro']);$cidade=$db->real_escape_string($_POST['cidade']);$estado=$db->real_escape_string($_POST['estado']);$cep=$db->real_escape_string($_POST['cep']);$sql="UPDATE amigos SET nome = '$nome', endereco = '$endereco', bairro = '$bairro', cidade = '$cidade', estado = '$estado', cep = '$cep' WHERE id = $id";if($db->query($sql)){$db->close();header("Location: index.php");exit();}else{$db->close();echo "Erro ao editar o amigo. Tente novamente.";}} ?><!doctypehtml><html lang="pt-BR"><head><meta charset="UTF-8"><meta content="width=device-width,initial-scale=1"name="viewport"><title>Editar Amigo</title><link href="css/styles.css"rel="stylesheet"></head><body><div class="container"><div class="form-container"><?php if(isset($_GET['id'])){$id=$_GET['id'];$sql="SELECT * FROM amigos WHERE id = $id";$result=$db->query($sql);$row=$result->fetch_assoc();if($row){ ?><form action="update.php"method="post"><input value="<?php echo $row['id']; ?>"name="id"type="hidden"><label for="nome">Nome:</label><input value="<?php echo $row['nome']; ?>"name="nome"id="nome"required><label for="endereco">Endereço:</label><input value="<?php echo $row['endereco']; ?>"name="endereco"id="endereco"required><label for="bairro">Bairro:</label><input value="<?php echo $row['bairro']; ?>"name="bairro"id="bairro"required><label for="cidade">Cidade:</label><input value="<?php echo $row['cidade']; ?>"name="cidade"id="cidade"required><label for="estado">Estado:</label><input value="<?php echo $row['estado']; ?>"name="estado"id="estado"required><label for="cep">CEP:</label><input value="<?php echo $row['cep']; ?>"name="cep"id="cep"required><input value="Atualizar Amigo"type="submit"><a href="index.php">Voltar</a></form><?php }else{echo "<p>Amigo não encontrado.</p>";}}else{echo "<p>ID não fornecido.</p>";} ?></div></div><footer><p>Universidade de Vassouras - Campus Maricá/RJ - 26/06/2024</p><p>Curso: Engenharia de Software - Turma: B</p><p>Disciplina: Laboratório de Programação Back End</p><p>Professor: Vitor Cesar</p><p>Equipe: Fábio Correia Lima (202212176) e Hygor Rasec de SantAnna Machado (202212020)</p></footer></body></html>